INSERT INTO `floodevent_indicator_past1980_timeperiods` VALUES ('608', '2005-07-05', 'BG', 'BG2PRPF100', 'с. Комарево, 02 юли 2005 г.', 'BG2FLPR014', 'р. Главница \n с. Комарево, Община Провадия', 'past', '2005', '07', '02', '2005-07-02', '2005-07-05', '3', null, null, 'Not Applicable', null, 'Unknown', 'Pluvial', 'No data', 'No data', null, null, null, 'Not Applicable', 'Not Applicable', 'Not Applicable', 'Property,Infrastructure,Rural Land Use', 'Art_4', 'BG2000', 'Дъждове. От проливни дъждове на 02-04.07.2005 г. на територията на Шуменска и Варненска области се формират високи вълни по поречията на р.Главница, Провадийска и Ана дере и техните притоци. По Ана дере преминава висока вълна на 03 и 04.07. Село Комарево е залято в 3.00 ч. през нощта на 04.07. Водата прелива първо през дигите на скатов събирател в селото, който зауства директно без шлюз в р.Ана дере,а  в последствие и през дясната дига на реката, която в този участък е по-ниска от лявата дига с около 30 см на фронт от 1100м.  ', null, null);
INSERT INTO `floodevent_indicator_past1980_timeperiods` VALUES ('609', '2005-07-05', 'BG', 'BG2PRPF101', 'с. Блъсково, 02 юли 2005 г.', 'BG2FLPR013', 'р. Главница \n с. Блъсково, Община Провадия', 'past', '2005', '07', '02', '2005-07-02', '2005-07-05', '3', null, null, 'Not Applicable', null, 'Unknown', 'Pluvial', 'No data', 'No data', null, null, null, 'Not Applicable', 'Not Applicable', 'Not Applicable', 'Property,Infrastructure,Rural Land Use', 'Art_4', 'BG2000', 'Дъждове. От проливни дъждове на 02-04.07.2005 г. на територията на Шуменска и Варненска области се формират високи вълни по поречията на р.Главница, Провадийска и Ана дере и техните притоци. Яз.Снежина прелива с макс.количества 60м3/с.От корегираните дерета при с.Черноок, Славейково и Китен постъпват водни маси не по-малко от 120 м3/с. В резултат с.Блъсково е залято в ниската си част на 03.07 около 16.30 ч., като реката прелива на участък около 1300м тотално през двете си диги без да ги къса. ', null, null);
