INSERT INTO `floodevent_indicator_past1980_timeperiods` VALUES ('621', '1999-09-05', 'BG', 'BG2SEPF390', 'с. Равнец, 02 - 04 септември 1999 г.', 'BG2FLSE020', 'р. Чукарска \n с. Равнец, Община Бургас', 'past', '1999', '09', '02', '1999-09-02', '1999-09-05', '3', null, null, 'Not Applicable', null, 'Unknown', 'Pluvial', 'No data', 'No data', null, null, null, 'Not Applicable', 'Not Applicable', 'Not Applicable', 'Property', 'Art_4', 'BG2000', 'материални щети за 2 390 000 лв. Бедствено положение, едно семейство е настанено във военните общежития. Село Равнец и землището му са напълно наводнени в следствие скъсване на дигите на двата язовира до село Винарско. Височината на водния стълб достига два метра и 60 сантиметра. Напълно са наводнени 263 къщи, от които необитаеми са 40, а седем са застрашени от срутване. Други 140 къщи са залети с тиня. Големи загуби са отчетени в селското стопанство. Има удавени животни.  От силния порой са изровени телефонните кабели и няма телефонна връзка със село Равнец. /нюз.бг 07.09.1999, 16:30 /', null, null);
