INSERT INTO `floodevent_indicator_past1980_timeperiods` VALUES ('630', '2009-09-09', 'BG', 'BG2SEPF409', 'гр. Несебър, 07 септември 2009 г.', 'BG2FLSE026', 'гр. Несебър, Община Несебър', 'past', '2009', '09', '07', '2009-09-07', '2009-09-09', '2', null, null, 'Not Applicable', null, 'Unknown', 'Pluvial', 'No data', 'No data', null, null, null, 'Not Applicable', 'Not Applicable', 'Not Applicable', 'Property,Infrastructure', 'Art_4', 'BG2000', 'Проливен дъжд. Наводнени приземни помещения на хотели между с. Равда и гр. Несебър най-малко валежи има в Несебър - между 9-10 литра на квадратен метър. Въпреки това, заради лоша инфраструктура на отводняването и проблеми със запушени канализации и шахти вътрешният път между Равда и Несебър е частично наводнен и е временно затворен за движение. Три хотела между Равда и Несебър са наводнени заради падналия дъжд. „Наводнени са приземните етажи на хотелите „Делфин”, „Копа Кабана” и „Аурелия”, предаде БГНЕС. Изпратени са три екипа, които в момента отводняват с моторни помпи. Няма пострадали туристи, каза  инж. Йордан Лозев. Паднало дърво е притиснало лека кола в Несебър, но няма пострадали.', null, null);
