INSERT INTO `floodevent_indicator_past1980_timeperiods` VALUES ('2067', '1991-01-11', 'ES', 'ES010-OU-06-02_19910110-19910110', 'Inundación Enero de 1991', 'ES010-OU-06-02', 'DEMARCACION HIDROGRAFICA DEL MINO-SIL LIMIA', 'past', '1991', '01', '10', '1991-01-10', '1991-01-11', '1', null, null, 'Unknown', null, null, 'Fluvial', 'Natural exceedance', 'Other', null, null, null, 'Not Applicable', 'Not Applicable', 'Not Applicable', 'Rural Land Use', 'Art_4', 'ES010', 'Ene 1991. Fuerte temporal de viento y agua en Galicia. Otro de los grandes cauces gallegos, el del Miño, anegó algunas tierras de labor, aunque ayer por la tarde únicamente había causado pérdidas en la agricultura.    Fuente: ABC_910111', null, 'L');
