INSERT INTO `floodevent_indicator_past1980_timeperiods` VALUES ('3831', '2005-01-10', 'FI', 'FITulvaTietoId145', '', 'FIPaikkaId6278', 'Helsingin ja Espoon rannikkoalue', 'past', '2005', '01', '08', '2005-01-08', '2005-01-10', '2.00', null, null, '110', 'Very Rare', null, 'Sea water', 'Natural exceedance', 'Other rapid onset', null, null, null, 'Human Health overall,Human Health', 'Not Applicable', 'Not Applicable', 'Economic,Property,Infrastructure', 'Art_4', 'FIVHA2', 'Itämeren Gudrun -talvimyrskyn seurauksena merivesi nousi Helsingissä korkeimmalle havaitulle tasolle N60 +1,45 m. Tulvavesi nousi mm. Helsingin kauppatorille ja kymmeniin taloihin, katkaisi useita teitä ja esti mm. Suomenlinnan lauttaliikenteen.', null, null);
INSERT INTO `floodevent_indicator_past1980_timeperiods` VALUES ('3832', '2007-08-14', 'FI', 'FITulvaTietoId164', '', 'FIPaikkaId6518', 'Porin keskusta ja pohjoispuoli', 'past', '2007', '08', '12', '2007-08-12', '2007-08-14', '2.00', null, null, 'Unknown', null, '15.000', 'Pluvial', 'Natural exceedance', 'Other rapid onset', null, null, null, 'Human Health overall,Human Health,Community', 'Not Applicable', 'Not Applicable', 'Economic,Property,Infrastructure,Economic Activity', 'Art_4', 'FIVHA3', 'Porin keskustan ylitti 12.8. etelä-pohjoissuunnassa voimakas sadealue. Se oli suhteellisen kapea (noin 3 km) ja selvärajainen. Pääosa sateesta tuli kello 16 ja 19 välillä. Sademäärä oli paikoin jopa 140 mm ja vettä kertyi nopeasti kaduille kymmeniä senttejä, alikulut täyttyivät jakaen kaupungin kahtia ja yli tuhat kiinteistöä kärsi vesivahinkoja. Pahimmin kärsinyt alue ulottui 6. osasta keskustan ja 5.kaupunginosan kautta Toejoelle ja Ruosniemeen. Vahingoiltaan kyseessä on Suomen suurin hulevesitulva. Tulvatapahtuma oli erittäin harvinainen (toistumisaika noin kerran 5000 vuodessa). (http://www.pori.fi/material/attachments/hallintokunnat/tekninenpalvelukeskus/ajankohtaistaliikenteesta/raportit/5vA4Hx8Kn/Kaupunkitulvaraportti-lopullinen-22102009.pdf).', null, null);
INSERT INTO `floodevent_indicator_past1980_timeperiods` VALUES ('3833', '2004-08-02', 'FI', 'FITulvaTietoId19', '', 'FIPaikkaId5810', 'Riihimäen keskusta', 'past', '2004', '07', '27', '2004-07-27', '2004-08-02', '6.00', null, null, '50', 'Rare', null, 'Fluvial', 'Natural exceedance', 'Medium onset flood', null, null, null, 'Human Health overall,Human Health', 'Environment,Waterbody Status,Pollution Sources', 'Not Applicable', 'Economic,Property,Infrastructure,Rural Land Use', 'Art_4', 'FIVHA2', 'Pitkäaikaisen sadejakson jälkeen voimakas matalapaine asettui eteläisen Suomen päälle. Vantaajoen latvaosilla satoi 27.7- 31.7 välillä 152 mm. Voimakkaimmin satoi 27.- 28.7 110 mm. Toistuvuus oli keskimäärin kerran 50 vuodessa.', null, null);
INSERT INTO `floodevent_indicator_past1980_timeperiods` VALUES ('3834', '2005-05-30', 'FI', 'FITulvaTietoId87', '', 'FIPaikkaId6178', 'Ivalon taajama', 'past', '2005', '05', '24', '2005-05-24', '2005-05-30', '6.00', null, null, '70', 'Very Rare', null, 'Fluvial', 'Natural exceedance', 'Snow melt flood,Other rapid onset', null, null, null, 'Human Health overall', 'Not Applicable', 'Not Applicable', 'Economic,Property,Infrastructure', 'Art_4', 'FIVHA7', 'Vuoden 2005 kevättulvan virtaamat Ivalojoen Pajakoskella aloittivat nopean kasvun toukokuun puolivälissä. Pajakosken aseman vedenkorkeuden maksimi N60 + 128,40 m saavutettiin 26.5 klo 03.35 ja Ivalon sillan asteikon vedenkorkeuden maksimi N60 + 122,98 m saavutettiin samana päivänä klo 12.00. Vuoden 2005 kevään tulva on suurin Ivalon alueella tapahtunut tulva.', null, null);
INSERT INTO `floodevent_indicator_past1980_timeperiods` VALUES ('3835', '2005-05-31', 'FI', 'FITulvaTietoId92', '', 'FIPaikkaId6179', 'Kittilän kirkonkylä', 'past', '2005', '05', '25', '2005-05-25', '2005-05-31', '6.00', null, null, '70', 'Very Rare', null, 'Fluvial', 'Natural exceedance', 'Snow melt flood,Medium onset flood', null, null, null, 'Human Health overall,Community', 'Not Applicable', 'Not Applicable', 'Economic,Property,Infrastructure', 'Art_4', 'FIVHA5', 'Lumen sulamisesta aiheutunut kevättulva Kittilän kirkonkylän alueella. Tulva aiheutti noin 6 miljoonan euron vahingot kunnalle. Tulvassa kastui useita rakennuksia ja tieyhteyksiä oli poikki. Erityiskohteista vahinkoja aiheutui Kittilän vanhainkodille, jossa osa rakennuksista kastui ja asukkaita jouduttiin evakuoimaan.', null, null);
