INSERT INTO `floodevent_indicator_past1980_timeperiods` VALUES ('6677', '1990-11-03', 'SI', 'SI1_199011A', 'Poplave novembra 1990_Porečje Save', 'SI1_199011A', 'Poplave novembra 1990_Porečje Save', 'past', '1990', '11', '01', '1990-11-01', '1990-11-03', '2', null, null, '50-100', 'Very Rare', null, 'Fluvial', 'Natural exceedance,Blockage/restriction', 'Flash flood,Other rapid onset', '1', null, null, 'Human Health,Other human health', 'Not Applicable', 'Not Applicable', 'Property,Rural Land Use,Infrastructure,Economic Activity', 'Art_4', 'SI_RBD_1', 'Katastrofalne poplave v Sloveniji, po nekaterih območjih največje v tem stoletju: Sora, Kamniška Bistrica, Savinja. Posledice: Sora je v Suhi narasla za več kot 4 m. Odneslo je vodomerno postajo Suha, ko je zabeležila vodostaj 5,78 m nad normalo. Na škofjeloškem območju se je utrgalo okrog 400 zemeljskih plazov, poškodovanih ali uničenih je bilo prek 34000 m2 v zgradbah in uničenih 52 ha kmetijskih zemljišč. Kamniška Bistrica s hudournimi pritoki in plazovi je uničila ali poškodovala prek 250 stanovanjskih objektov, v občini Domžale pa uničila tudi 300 m in poškodovala 20 km cest. Savinja s pritoki se je v Nazarjah in Laškem dvignila za skoraj 6 m nad normalo in dosegla maksimalni pretok v Laškem 1400 m3/s. Savinja s pritoki je med Lučami in Ljubnim porušila vse štiri mostove, brvi in jezove, poplavila sedem domačij, štiri pa so odnesli ali močno poškodovali zemeljski plazovi. Lučnica je zaradi zajezitve ob zemeljskem plazu preplavila 3 hiše. Še vedno smatramo poplavni dogodek iz leta 1990 kot dogodek z največjimi posledicami za družbo. Ujma je zajela 2/3 ozemlja Slovenije, razen območja Mure in Primorja. V vplivnem območju nevarnosti je bilo 240.000 ljudi, izseljeno 237, evakuirano 2600, uničenih 190 objektov, poplavljenih 5231 objektov, poplavjeno 398 industrijskih objektov, porušenih 96 mostov, poškodovanih 280 mostov, poškodovanih 2683 km cest, uničeno 20 km železniške proge, sproženo 480 zemeljskih plazov, registriranih 2000 zdrsov. Neposredna škoda je znašala 1.134.000.000 DEM.\nNajvečji delež škode je utrpelo gospodarstvo 28% celotne škode. Največji delež škode (62%) je utrpelo območje Savinja-Sotla. \n', null, null);
