INSERT INTO `floodevent_indicator_past1980_timeperiods` VALUES ('6739', '2007-09-19', 'SI', 'SI3_200709A', 'Poplave septembra 2007_Porečje Drave', 'SI3_200709A', 'Poplave septembra 2007_Porečje Drave', 'past', '2007', '09', '18', '2007-09-18', '2007-09-19', '1', null, null, '5-50', 'Rare', null, 'Fluvial', 'Natural exceedance', 'Flash flood', 'Unknown', null, null, 'Human Health', 'Not Applicable', 'Not Applicable', 'Property,Rural Land Use,Infrastructure,Economic Activity', 'Art_4', 'SI_RBD_1', 'V septembru so nastali obsežni konvektivni sistemi, tvorili so močne nevihte, ki so dlje časa vztrajale na istem območju. Posledica so bili izredno veliki pretoki na malih in srednje velikih vodotokih. Poplave s hudourniškim značajem so se pojavile na območju 1/3 ozemlja Slovenije. Neposredna škoda je znašala 200 mio eur, 38% na vodni infrastrukturi, 83% celotne škode je bilo na območju Gorenjske in Savinje-Sotla. Poškodovanih je bilo 4329 stanovanjskih objektov, 979 gospodarskih objektov, 61 javnih zavodov, 192 podjetij, 347 km državnih in 1591občinskih cest, 147 mostov, 17 km vodovodnega omrežja, 7 km elektro omrežja, 48 vodnih zajetij, sproženo 432 zemeljskih plazov in 29 s plazovi ogroženih stavb. Večina škode je nastala v pasu 150 km krat 70 km. Življenje izgubilo 6 oseb. Neposredna škoda je znašala 200 mio €, od tega škoda na prometni infrastrukturi v občinah 32,8 mio €, škoda na objektih in gradbeno inženirskih objektih 20,5 mio €, škoda na osnovnih sredstvih 16,3 mio €, škoda na kmetijskih zemljiščih 3,7 mio €, škoda na državnih cestah 12,7 mio €, škoda na vodni infrastrukturi 76,3 mio €, škoda v gospodarstvo vključno s škodo na kulturni dediščini 34,3 mio €. Na porečju Drave je bilo škode na vodni infrastrukturi 2,145 mio €, na porečju Save 80,606 mio € ter na povodju Soče 8,726 mio €. Bača je poškodovala 91 km struge in 138 prečnih objektov, Sava Bohinjka in Selška Sora skupaj 371 km struge in 552 prečnih objektov, Savinja 224 km struge in 358 prečnih objektov, Dravinja poškodovala 56 km struge in Kamniška Bistrica 101 km struge in 111 prečnih objektov.\n\n', null, null);
